pr( 0019-2, ['¿Qué es combinatoria?'], ['La combinatoria es la parte de las Matematicas que se ocupa de la resolucion de problemas de eleccion y disposicion de los elementos de cierto conjunto.'], 20/20 , ['http://sauce.pntic.mec.es/~jpeo0002/Archivos/PDF/T01.pdf']).

pr( 0019-3, ['¿Qué es una permutacion?'], ['Una permutación es una combinación en donde el orden es importante.'], 20/20 , ['http://www.aaamatematicas.com/sta-permu.htm']).

pr( 0019-4, ['¿Qué son las variaciones?'], ['Se llama variaciones a los diferentes grupos que pueden formarce con cierta cantidad de elementos.'], 20/20 , ['http://www.ditutor.com/combinatoria/variaciones.html']).

pr( 0019-5, ['¿De que otra forma se le puede llamar a los numeros combinatorios?'], ['Coeficientes binomiales'], 20/20 , ['http://es.wikipedia.org/wiki/Coeficiente_binomial']).

pr( 0019-6, ['¿Como se le llama a las variaciones de orden maximo?'], ['Permutaciones'], 20/20 , ['http://aula.iescuravalera.es/']).

