pr(00254-1,['¿Qué es espacio vectorial?'], ['Un espacio vectorial es una estructura algebraica creada a partir de un conjunto no vacío, una operación interna (llamada suma, definida para los elementos del conjunto) y una operación externa (llamada producto por un escalar, definida entre dicho conjunto y otro conjunto, con estructura de cuerpo ), con 8 propiedades fundamentales.'],20/20,['Angel Uzcategui']).
pr(00254-2,['¿Qué es un vector?'], ['Es un elemento de un espacio vectorial'],20/20,['Angel Uzcategui']).
pr(00254-3,['¿Cémo se les llama a los eslementos del cuerpo de un espacio vectorial?'], ['Escalares'],20/20,['Angel Uzcategui']).
pr(00254-4,['¿Qué ideas condujeron a los espacios vectoriales?'], ['se toman teorias de geometría analítica, matrices y sistemas de ecuaciones lineales.'],20/20,['Angel Uzcategui']).
